	    			}	    		}				 				//$("#photosName"+i).text(fileinfo.name);				$("#photosName"+i).attr("src",fileinfo.small_url);				$("#photos"+i).css("display","block");	    		$("#esinfoform-photos"+i).attr("value",JSON.stringify(fileinfo));	     	    		$("#esinfoform-photosCount").attr("value",count);	    	 		    }	 		    //上传logo成功		    else		    {		    	$("#Imageurl").attr("src",fileinfo.small_url);				$("#esinfoform--Imageurl").val(JSON.stringify(fileinfo));			 			    }	    		    }else{			//alert('上传失败');			showAlert("pic_error", state+"<br/>"+str);	    }	    	}		function file_del(id,type){		//重置logo		if(type=='logo')		{   if($("tempImageurl").val()==null||$("tempImageurl").val()=="")		{ $("#Imageurl").attr("src","images/eshop/banner.png");			}		else{ 						$("#Imageurl").attr("src",$("tempImageurl").val());			}						$("#Imageurl").attr("src",$("tempImageurl").val());			$("#esinfoform--Imageurl").val("");	    	 			}		//公司图片		else		{					$("#photos"+id).css("display","none");		$("#esinfoform-photos"+id).val("");		//$("#photosName"+id).attr("src",$("#esinfoform-tempphotos"+id).val());		var count = $("#esinfoform-photosCount").attr("value")-1;		$("#esinfoform-photosCount").attr("value",count);		if(count < 9){			$("#upload_photos").css("display","block");		}		}	}	</script><div class="outwrap">			<div class="innerwrap bg_form">				<div class="innerCotent">						<div class="mo-tabs">			<span style="color:red;"><?php if(isset($tip))echo $tip;$tip="";   ?></span>		    <ul id="myTab" class="nav nav-tabs" role="tablist">		      <li role="presentation" class="active"><a href="#home" id="home-tab" role="tab" data-toggle="tab" aria-controls="home" aria-expanded="true">电商模版信息</a></li>		      <li role="presentation" class=""><a href="#profile" role="tab" id="profile-tab" data-toggle="tab" aria-controls="profile" aria-expanded="false">产品信息</a></li>		      		    </ul>		    <div id="myTabContent" class="tab-content">